mapper.update:
UPDATE MEMBER
SET PWD = #{pwd}, ADDRESS = #{address}, EMAIL = #{email}, PHONE = #{phone}
WHERE MEMBER_ID = #{memberId}

mapper.select:
SELECT MEMBER_ID, USER_NAME, NAME, ADDRESS, EMAIL, PHONE, PWD
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT MEMBER_ID, USER_NAME, NAME, ADDRESS, EMAIL, PHONE, PWD
FROM MEMBER
WHERE USER_NAME = #{userName}
